package com.syntax.class07;

public class Task3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int n=100;
while(n>=1) {
	if(n%2==1) {
		System.out.print(n+" ");
	}
	n--;
}
System.out.println("------");
int n1=99;
while(n1>=1) {
	System.out.print(n1+" ");
	n1-=2;
	}
	}

}
