package com.synyax.class03;

public class ifElse7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double bb1=100;
		double bb2=300;
		if(bb1>bb2) {
			System.out.println("bb1 "+bb1+" is larger then bb2 "+bb2);
		} else {
			System.out.println("bb2 "+bb2+" is larger than bb1 "+bb1);
		}
	}

}
